module RGB565to888(
	rgb565,
	rgb888);

	//---Ports declearation: generated by Robei---
	input [15:0] rgb565;
	output [23:0] rgb888;

	wire [15:0] rgb565;
	wire [23:0] rgb888;

	//----Code starts here: integrated by Robei-----
	
	/**********************************************************************************************************************************/
	/****************************	RGB565 to RGB888	*************************************************************************/
	/*	For instance	:	**************************************************************************************************************/
	/*********************************************************************************************************************************/
	/*****	16bit RGB656 R4 R3 R2 R1 R0 G5 G4 G3 G2 G1 G0 B4 B3 B2 B1 B0	***************************************/
	/**********************************************************************************************************************************/
	/*****	24ibt RGB888 R4 R3 R2 R1 R0 R2 R1 R0 G5 G4 G3 G2 G1 G0 G1 G0 B4 B3 B2 B1 B0 B2 B1 B0	********/
	/**********************************************************************************************************************************/
	
	
	
	assign		rgb888[4:0]		=		rgb565[4:0];
	
	assign		rgb888[7:5]		=		rgb565[4:2];
	
	assign		rgb888[13:8]		=		rgb565[10:5];
	
	assign		rgb888[15:14]		=		rgb565[10:9];
	
	assign		rgb888[20:16]		=		rgb565[15:11];
	
	assign		rgb888[23:21]		=		rgb565[15:13];
	
	
endmodule    //RGB565to888

